*         LCTR(IBAS) = NCTR                                             G88DRV
*         LANG(IBAS) = LISTP(I)                                         G88DRV
*  40   CONTINUE                                                        G88DRV
C                                                                       G88DRV
C  d orbitals:                                                          G88DRV
C                                                                       G88DRV
*  50   IF(MAXL.NE.2) GOTO 70                                           G88DRV
*         IMAX = I5D6D + 5                                              G88DRV
*         KD = I5D6D + 1                                                G88DRV
*         DO 60 I = 1,IMAX                                              G88DRV
*           IBAS = IBAS + 1                                             G88DRV
*           LCTR(IBAS) = NCTR                                           G88DRV
*           LANG(IBAS) = LISTD(I,KD)                                    G88DRV
*  60     CONTINUE                                                      G88DRV
