*       SEQ = .FALSE.                                                   G88DRV
*       CALL NBCLOS(SEQ)                                                G88DRV
*     END IF                                                            G88DRV
*     RETURN                                                            G88DRV
C                                                                       G88DRV
* 900 FORMAT(/1X,'Error opening the NBO direct access file in ',        G88DRV
*    + 'subroutine DELSCF.')                                            G88DRV
*     END                                                               G88DRV
