*       IF(IDONE.EQ.0) CALL DELSCF(CORE,CORE,NBOOPT)                    G82DRV
C                                                                       G82DRV
*     ELSE IF(NBOOPT(1).EQ.3) THEN                                      G82DRV
*       CALL TREAD(636,CORE,81,1,81,1,0)                                G82DRV
*       DO 40 I = 1,80                                                  G82DRV
*         FILENM(I:I) = CHAR(INT(CORE(I) + TENTH))                      G82DRV
*  40   CONTINUE                                                        G82DRV
*       LFNDAF = INT(ABS(CORE(81)) + TENTH)                             G82DRV
